mapper.update:
        update friend


                status = #{status, jdbcType=INTEGER},


                mark = #{mark, jdbcType=VARCHAR}


				deleted = #{deleted, jdbcType=TINYINT}


        where owner = #{owner, jdbcType=VARCHAR} and friend = #{friend, jdbcType=VARCHAR}

mapper.select:
select  from friend where deleted = 0 and owner = #{owner, jdbcType=VARCHAR}


		friend = #{friend, jdbcType=VARCHAR} and


		status = #{status, jdbcType=TINYINT} and
